SELECT a.nama 'nama_alternatif', k.nama 'nama_kriteria', n.nilai 'nilai', k.bobot 'bobot', k.kategori 'kategori' FROM nilai_kriteria n JOIN alternatif a ON (n.id_alternatif=a.id IN((SELECT id from alternatif WHERE id_asisten IN(SELECT id from asisten WHERE id_tahun_ajaran = (SELECT id from tahun_ajaran WHERE status='1') AND tipe='Praktikum')))) JOIN kriteria k ON(k.id=n.id_kriteria)
